<script type="text/javascript">
    var initTranslator = function (cb) {
        Sunlight.loadJS('https://translate.yandex.net/website-widget/v1/widget.js?widgetId=ytWidget&pageLang=ru&widgetTheme=light&autoMode=false', function () {
            if (cb) {
                cb();
            }

            $(function () {
                var $ytw = $('#ytWidget');
                var lan_code = Cookies.get('language_code');
                var lan_selector = $('.last-line-language ul li > a');
                if (lan_code && lan_code != 'ru') {
                    lan_selector.each(function () {
                        $(this).removeClass('nonactive');
                        if ($(this).data('language-id') === lan_code) {
                            $(this).addClass('nonactive');
                        }
                    });
                    $ytw.css('display', 'inline-block');
                }
                lan_selector.bind('click', function (e) {
                    e.preventDefault();
                    var languageId = $(e.currentTarget).data('language-id');
                    var ytwSwitcher = $('.yt-button__icon_type_right');
                    var is_local_ip = 'False';
                    if (is_local_ip !== 'True') {
                        $.get(window.SUNLIGHT.API_URL + '/t/event/?type=change_language_click&value=' + languageId);
                    }

                    lan_selector.each(function () {
                        $(this).removeClass('nonactive')
                    });
                    $(e.currentTarget).addClass('nonactive');
                    if (languageId === 'ru') {
                        ytwSwitcher.click();
                        $ytw.hide();
                        Cookies.remove('language_code');
                    } else {
                        Cookies.set('language_code', languageId);
                        $ytw.css('display', 'inline-block');
                        ytwSwitcher.click();
                        $('form.yt-listbox [value="' + languageId + '"]').next('.yt-listbox__text').click();
                    }
                });
            });
        });
    };
    if (Cookies.get('language_code')) {
        initTranslator();
    } else {
        var translatorLoaded = false;
        var onScroll = _.throttle(function () {
            var scroll = document.body.scrollTop || document.documentElement.scrollTop;
            if (scroll >= document.body.scrollHeight - window.innerHeight - 100) {
                translatorLoaded = true;
                initTranslator(function () {
                    window.removeEventListener('scroll', onScroll);
                })
            }
        }, 100);

        window.addEventListener('scroll', onScroll);
    }
    window.addEventListener('scroll', function () {
        $('.featherlight .select-menu').selectmenu('close')
    }, {passive: true});
    if (!SunlightBase.isMobileDevice) {
        $('.featherlight .select-menu').each(function () {
            var $element = $(this);
            $($element).selectmenu({
                width:  '99%',
                create: function (e, ui) {

                },
                open:   function (e, ui) {
                    var menuEl   = $('#' + e.target.id + '-menu'),
                        popupEl  = $element.parents('.featherlight'),
                        buttonEl = $('#' + e.target.id + '-button');
                    menuEl.width(buttonEl.width() - 2);
                    menuEl.height($(window).height() - buttonEl.offset().top + popupEl.offset().top - buttonEl.height() - 5)
                },

                select: function (e, ui) {
                    $(ui).addClass('ui-state-selected');
                },
                change: function () {
                    $element.trigger('change');
                }
            });
        });
    }

    $('input.input-phone').mask('+7 (999) 999-99-99');
    emailProviders = {
        "gmail.com":  "mail.google.com",
        "mail.ru":    "mail.ru",
        "list.ru":    "mail.ru",
        "bk.ru":      "mail.ru",
        "inbox.ru":   "mail.ru",
        "rambler.ru": "mail.rambler.ru",
        "ya.ru":      "mail.yandex.ru",
        "yandex.ru":  "mail.yandex.ru",
        "narod.ru":   "mail.yandex.ru"
    };

</script>

