                        <select name="count"
                                data-id="<%= id %>"
                                class="js-cart-count"
                                id="count_<%= id %>">
                            <option></option>
                            <% counts.forEach(function (c) { %>
                            <option value="<%= c %>"
                            <% if (count===c){ %>selected<% } %> ><%= c %>
