    <script type="text/template" id="product-of-the-day-tpl">
    <% if (product && products) products.unshift(product) %>
<% if (products && products.length) { %>
    <% if (products.length > 5) products.splice(5) %>
    <a href="/product-of-the-day/" class="countdown">
        <div class="caption">Товар дня</div>
        <div class="js-timer timer">
            <div class="js-h item">00</div> : <div class="js-m item">00</div> : <div class="js-s item">00</div>
        </div>
    </a>
    <section class="day-products main-page-slider-cont owl-carousel">
        <% products.forEach(function(product) { %>
            <a href="<%= product.absolute_url %>" class="day-product <%= product.remains_amount === 0 ? 'none' : '' %>">
                <div class="day-product-img">
                    <div class="lazy-load-wrapper">
                        <img data-src="<%= product.image.url %>"
                             src="" alt="" class="lazy-load">
                        <% if (product.remains_amount === 0) { %>
                            <div class="sold"></div>
                        <% } %>
                    </div>
                </div>
                <div class="day-prices">
                    <% if (product.prices.initial) { %>
                        <div class="final <%= product.remains_amount ? 'm-red' : 'm-gray' %>">
                            <%= product.prices.final %> р.
                        </div>
                    <% } %>
                    <% if (product.prices.initial) { %>
                        <div class="discount m-gray">
                            <%= product.prices.initial %> р.
                        </div>
                    <% } %>
                    <% if (product.remains_amount) { %>
                        <div class="day-remains <%= product.remains_amount > 30 ? 'lt50' : '' %>">
                            осталось<br><%= product.remains_amount > 30 ? 'меньше<br>' : '' %><span class="m-red"><%= product.remains_amount <= 30 ? product.remains_amount < 10 ? '&nbsp;&nbsp;&nbsp;' + product.remains_amount : product.remains_amount : '50' %></span> шт.
                        </div>
                    <% } %>
                </div>
            </a>
        <% }) %>
    </section>
<% } %>
</script>



